import React from 'react';

const SKILLS = [
  {
    category: "Programming Languages",
    items: [
      { name: "Python", icon: "/assets/skills/python.svg" },
      { name: "Java", icon: "/assets/skills/java.svg" },
      { name: "C / C++", icon: "/assets/skills/cpp.svg" },
      { name: "JavaScript", icon: "/assets/skills/javascript.png" },
      { name: "Swift (basic)", icon: "/assets/skills/swift.svg" }
    ]
  }
];

export default function Skills() {
  return (
    <section id="skills" className="skills-section skills-bg">
      <div className="skills-header">
        <h2>Technical Skills</h2>
        <span className="muted">A quick glance at my expertise</span>
      </div>
      <div className="skills-categories">
        {SKILLS.map(category => (
          <div className="skills-category" key={category.category}>
            <div className="skills-category-title">{category.category}</div>
            <div className="skills-grid">
              {category.items.map(skill => (
                <div className="skill-item" key={skill.name}>
                  <span className="skill-badge">
                    {skill.icon ? (
                      <img src={skill.icon} alt={skill.name} loading="lazy" />
                    ) : (
                      <span className="skill-fallback">{skill.name[0]}</span>
                    )}
                  </span>
                  <span className="skill-name">{skill.name}</span>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}
