import React from 'react';

/*
  Skills section styled with wave background and logo grid.
  Place svg/png logos in public/assets/skills (e.g. public/assets/skills/react.svg).
  If a logo file is missing the component will show a fallback initial.
*/

const SKILLS = [
  { name: 'React', file: '/assets/skills/react.svg' },
  { name: 'React Native', file: '/assets/skills/react-native.svg' },
  { name: 'Tailwind CSS', file: '/assets/skills/tailwind.svg' },
  { name: 'HTML5', file: '/assets/skills/html5.svg' },
  { name: 'CSS3', file: '/assets/skills/css3.svg' },
  { name: 'Bootstrap', file: '/assets/skills/bootstrap.svg' },
  { name: 'JavaScript', file: '/assets/skills/javascript.svg' },
  { name: 'Figma', file: '/assets/skills/figma.svg' },
  { name: 'Node.js', file: '/assets/skills/nodejs.svg' },
  { name: 'Express', file: '/assets/skills/express.svg' },
  { name: 'MongoDB', file: '/assets/skills/mongodb.svg' },
  { name: 'Socket.io', file: '/assets/skills/socketio.svg' },
];

export default function Skills() {
  return (
    <section id="skills" className="skills-section reveal">
      <div className="skills-wave skills-wave-top" aria-hidden="true" />
      <div className="container skills-inner">
        <div className="skills-header">
          <h2>My Skills</h2>
          <p className="muted">MERN 路 React.js 路 React Native 路 UX/UI 路 Figma</p>
        </div>

        <div className="skills-grid" role="list">
          {SKILLS.map((s) => (
            <div key={s.name} className="skill-item" role="listitem">
              <div className="skill-badge" title={s.name}>
                <img
                  src={s.file}
                  alt={`${s.name} logo`}
                  onError={(e) => {
                    // hide broken image and let fallback show
                    e.currentTarget.style.display = 'none';
                  }}
                />
                <div className="skill-fallback" aria-hidden="true">
                  {s.name.split(' ').map((p) => p[0]).slice(0,2).join('')}
                </div>
              </div>
              <div className="skill-name">{s.name}</div>
            </div>
          ))}
        </div>
      </div>
      <div className="skills-wave skills-wave-bottom" aria-hidden="true" />
    </section>
  );
}
