import React from 'react';

const SKILLS = [
  {
    name: "Frontend Development",
    icon: "/assets/skills/frontend.svg",
    desc: "Building interactive and responsive web interfaces using React, HTML5, CSS3, and JavaScript.",
    color: "#2563eb"
  },
  {
    name: "Backend Development",
    icon: "/assets/skills/backend.svg",
    desc: "Creating robust APIs and server-side logic with Node.js, Express, and MongoDB.",
    color: "#059669"
  },
  {
    name: "UI/UX Design",
    icon: "/assets/skills/uiux.svg",
    desc: "Designing user-centric experiences and beautiful interfaces with Figma and design systems.",
    color: "#f59e42"
  },
  {
    name: "Mobile Apps",
    icon: "/assets/skills/mobile.svg",
    desc: "Developing cross-platform mobile applications using React Native.",
    color: "#60a5fa"
  },
  {
    name: "Version Control",
    icon: "/assets/skills/github.svg",
    desc: "Managing codebases and collaboration with Git & GitHub.",
    color: "#64748b"
  },
  {
    name: "Problem Solving",
    icon: "/assets/skills/problem.svg",
    desc: "Solving complex challenges with creative and analytical thinking.",
    color: "#f87171"
  }
];

export default function Skills() {
  return (
    <section id="skills" className="skills-attractive-section container card">
      <h2 className="skills-attractive-title">My Skills</h2>
      <p className="skills-attractive-subtitle">What I Do Best</p>
      <div className="skills-attractive-grid">
        {SKILLS.map(skill => (
          <div className="skills-attractive-item" key={skill.name} style={{ borderTop: `4px solid ${skill.color}` }}>
            <div className="skills-attractive-icon" style={{ background: `${skill.color}22` }}>
              <img src={skill.icon} alt={skill.name} loading="lazy" />
            </div>
            <h3 className="skills-attractive-name" style={{ color: skill.color }}>{skill.name}</h3>
            <p className="skills-attractive-desc">{skill.desc}</p>
          </div>
        ))}
      </div>
    </section>
  );
}
