import React from 'react';

export default function Hero() {
  return (
    <div className="container hero">
      <div className="hero-content">
        <h1>Hi, I’m Priyadharshini</h1>
        <p className="lead">
          Full‑Stack MERN Developer & UX/UI Designer — building responsive web apps with React, Node, MongoDB and designing delightful user experiences.
        </p>
        <div className="hero-cta">
          <a className="btn" href="#projects">See Projects</a>
          <a className="btn btn-outline" href="https://www.linkedin.com/in/priyadharshini2214" target="_blank" rel="noreferrer">LinkedIn</a>
        </div>
      </div>
    </div>
  );
}
