import React from 'react';

/*
  Modern, attractive Skills section:
  - Skills grouped by category, each with a colored card
  - Responsive grid, animated entry
  - Large circular logos, fallback initials
  - Clean, professional headings
*/

const SKILL_CATEGORIES = [
  {
    title: 'Frontend',
    color: 'linear-gradient(135deg, #60a5fa 0%, #818cf8 100%)',
    items: [
      { name: 'React', file: '/assets/skills/react.png' },
      { name: 'React Native', file: '/assets/skills/react-native.svg' },
      { name: 'HTML5', file: '/assets/skills/html5.png' },
      { name: 'CSS3', file: '/assets/skills/css3.svg' },
      { name: 'JavaScript', file: '/assets/skills/javascript.png' },
      { name: 'Tailwind', file: '/assets/skills/tailwind.svg' },
      { name: 'Bootstrap', file: '/assets/skills/bootstrap.svg' },
    ],
  },
  {
    title: 'Backend',
    color: 'linear-gradient(135deg, #34d399 0%, #06b6d4 100%)',
    items: [
      { name: 'Node.js', file: '/assets/skills/nodejs.svg' },
      { name: 'Express', file: '/assets/skills/express.svg' },
      { name: 'MongoDB', file: '/assets/skills/mongodb.svg' },
      { name: 'Socket.io', file: '/assets/skills/socketio.svg' },
    ],
  },
  {
    title: 'Design',
    color: 'linear-gradient(135deg, #f472b6 0%, #fbbf24 100%)',
    items: [
      { name: 'Figma', file: '/assets/skills/figma.png' },
      { name: 'UI/UX', file: '/assets/skills/figma.png' },
    ],
  },
  {
    title: 'Tools',
    color: 'linear-gradient(135deg, #a3e635 0%, #38bdf8 100%)',
    items: [
      { name: 'Git / GitHub', file: '/assets/skills/github.svg' },
      { name: 'Postman', file: '/assets/skills/postman.svg' },
    ],
  },
];

export default function Skills() {
  return (
    <section id="skills" className="skills-section reveal" aria-labelledby="skills-heading">
      <div className="skills-wave skills-wave-top" aria-hidden="true" />
      <div className="container skills-inner" role="region" aria-label="Skills">
        <div className="skills-header">
          <h2 id="skills-heading">My Skills</h2>
          <p className="muted">Full Stack · Mobile · UI/UX · Modern Tools</p>
        </div>
        <div className="skills-categories">
          {SKILL_CATEGORIES.map((cat) => (
            <div
              key={cat.title}
              className="skills-category"
              style={{ background: cat.color }}
            >
              <h3 className="skills-category-title">{cat.title}</h3>
              <div className="skills-grid" role="list">
                {cat.items.map((s) => (
                  <div
                    key={s.name}
                    className="skill-item"
                    role="listitem"
                    tabIndex={0}
                    aria-label={s.name}
                    title={s.name}
                  >
                    <div className="skill-badge" aria-hidden="true">
                      <img
                        src={s.file}
                        alt={`${s.name} logo`}
                        loading="lazy"
                        decoding="async"
                        onError={(e) => (e.currentTarget.style.display = 'none')}
                      />
                      <div className="skill-fallback" aria-hidden="true">
                        {s.name.split(' ').map((p) => p[0]).slice(0, 2).join('')}
                      </div>
                    </div>
                    <div className="skill-name">{s.name}</div>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
      </div>
      <div className="skills-wave skills-wave-bottom" aria-hidden="true" />
    </section>
  );
}
