import React from 'react';

const SKILLS = [
  {
    category: "Programming Languages",
    items: [
      { name: "Python", icon: "/assets/skills/python.svg" },
      { name: "Java", icon: "/assets/skills/java.svg" },
      { name: "C / C++", icon: "/assets/skills/cpp.svg" },
      { name: "JavaScript", icon: "/assets/skills/javascript.png" },
      { name: "Swift (basic)", icon: "/assets/skills/swift.svg" }
    ]
  },
  {
    category: "Web & App Development",
    items: [
      { name: "React.js / React Native", icon: "/assets/skills/react.png" },
      { name: "Node.js & Express.js", icon: "/assets/skills/nodejs.svg" },
      { name: "MERN Stack", icon: "/assets/skills/mern.svg" },
      { name: "Django", icon: "/assets/skills/django.svg" },
      { name: "RESTful API", icon: "/assets/skills/api.svg" },
      { name: "Full-Stack Web Development", icon: "/assets/skills/fullstack.svg" }
    ]
  },
  {
    category: "Databases",
    items: [
      { name: "MongoDB", icon: "/assets/skills/mongodb.svg" },
      { name: "SQL (basic)", icon: "/assets/skills/sql.svg" }
    ]
  },
  {
    category: "UI/UX Design",
    items: [
      { name: "Figma", icon: "/assets/skills/figma.png" },
      { name: "User Interface & Experience Design", icon: "/assets/skills/uiux.svg" },
      { name: "Interactive Navigation Design", icon: "/assets/skills/navigation.svg" }
    ]
  },
  {
    category: "DevOps & Tools",
    items: [
      { name: "Git & GitHub", icon: "/assets/skills/github.svg" },
      { name: "DevOps Foundations (CI/CD basics)", icon: "/assets/skills/devops.svg" },
      { name: "VS Code / Visual Studio", icon: "/assets/skills/vscode.svg" }
    ]
  },
  {
    category: "Other Technical Areas",
    items: [
      { name: "Data Science Basics", icon: "/assets/skills/datascience.svg" },
      { name: "Real-Time Applications", icon: "/assets/skills/socketio.svg" },
      { name: "Inventory Management Systems", icon: "/assets/skills/inventory.svg" }
    ]
  }
];

export default function Skills() {
  return (
    <section id="skills" className="skills-section skills-bg">
      <div className="skills-header">
        <h2>Technical Skills</h2>
        <span className="muted">A quick glance at my expertise</span>
      </div>
      <div className="skills-categories">
        {SKILLS.map(category => (
          <div className="skills-category" key={category.category}>
            <div className="skills-category-title">{category.category}</div>
            <div className="skills-grid">
              {category.items.map(skill => (
                <div className="skill-item" key={skill.name}>
                  <span className="skill-badge">
                    {skill.icon ? (
                      <img src={skill.icon} alt={skill.name} loading="lazy" />
                    ) : (
                      <span className="skill-fallback">{skill.name[0]}</span>
                    )}
                  </span>
                  <span className="skill-name">{skill.name}</span>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}
