import React from 'react';

const SKILLS = [
  {
    name: "Python",
    icon: "/assets/skills/python.svg",
    level: 5,
    desc: "Programming Language"
  },
  {
    name: "Java",
    icon: "/assets/skills/java.svg",
    level: 4,
    desc: "Programming Language"
  },
  {
    name: "C / C++",
    icon: "/assets/skills/cpp.svg",
    level: 4,
    desc: "Programming Language"
  },
  {
    name: "JavaScript",
    icon: "/assets/skills/javascript.png",
    level: 5,
    desc: "Programming Language"
  },
  {
    name: "Swift (basic)",
    icon: "/assets/skills/swift.svg",
    level: 2,
    desc: "Programming Language"
  },
  {
    name: "React.js / React Native",
    icon: "/assets/skills/react.png",
    level: 5,
    desc: "Web & App Development"
  },
  {
    name: "Node.js & Express.js",
    icon: "/assets/skills/nodejs.svg",
    level: 4,
    desc: "Web & App Development"
  },
  {
    name: "MERN Stack",
    icon: "/assets/skills/mern.svg",
    level: 5,
    desc: "Full-Stack Web Development"
  },
  {
    name: "Django",
    icon: "/assets/skills/django.svg",
    level: 3,
    desc: "Web & App Development"
  },
  {
    name: "RESTful API",
    icon: "/assets/skills/api.svg",
    level: 4,
    desc: "API Development"
  },
  {
    name: "MongoDB",
    icon: "/assets/skills/mongodb.svg",
    level: 4,
    desc: "Database"
  },
  {
    name: "SQL (basic)",
    icon: "/assets/skills/sql.svg",
    level: 2,
    desc: "Database"
  },
  {
    name: "Figma",
    icon: "/assets/skills/figma.png",
    level: 5,
    desc: "UI/UX Design"
  },
  {
    name: "UI/UX Design",
    icon: "/assets/skills/uiux.svg",
    level: 5,
    desc: "User Interface & Experience Design"
  },
  {
    name: "Interactive Navigation",
    icon: "/assets/skills/navigation.svg",
    level: 4,
    desc: "UI/UX Design"
  },
  {
    name: "Git & GitHub",
    icon: "/assets/skills/github.svg",
    level: 5,
    desc: "DevOps & Tools"
  },
  {
    name: "DevOps Foundations",
    icon: "/assets/skills/devops.svg",
    level: 3,
    desc: "CI/CD basics"
  },
  {
    name: "VS Code / Visual Studio",
    icon: "/assets/skills/vscode.svg",
    level: 5,
    desc: "DevOps & Tools"
  },
  {
    name: "Data Science Basics",
    icon: "/assets/skills/datascience.svg",
    level: 3,
    desc: "Python for Data Science"
  },
  {
    name: "Real-Time Applications",
    icon: "/assets/skills/socketio.svg",
    level: 4,
    desc: "Socket.io, Secure Chat Apps"
  },
  {
    name: "Inventory Management",
    icon: "/assets/skills/inventory.svg",
    level: 4,
    desc: "Systems"
  }
];

export default function Skills() {
  return (
    <section id="skills" className="skills-bar-section container card">
      <h2 className="skills-bar-title">Skills & Competencies</h2>
      <div className="skills-bar-grid">
        {SKILLS.map(skill => (
          <div className="skills-bar-item" key={skill.name}>
            <div className="skills-bar-header">
              <div className="skills-bar-icon">
                <img src={skill.icon} alt={skill.name} loading="lazy" />
              </div>
              <div>
                <span className="skills-bar-name">{skill.name}</span>
                <span className="skills-bar-level">{skill.level}</span>
              </div>
            </div>
            <div className="skills-bar-desc">{skill.desc}</div>
            <div className="skills-bar-progress">
              <div
                className="skills-bar-fill"
                style={{ width: `${skill.level * 20}%` }}
              ></div>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}
