import React from 'react';

const SKILLS = [
  {
    name: "Creative Direction",
    icon: "/assets/skills/creative.svg",
    desc: "Leading communication design, interactive design, and concept forward of creative work"
  },
  {
    name: "Graphic Design",
    icon: "/assets/skills/graphic.svg",
    desc: "The art of communication, stylizing, and problem-solving through the use of type and image"
  },
  {
    name: "Experience Design",
    icon: "/assets/skills/experience.svg",
    desc: "A focus placed on the quality of the user experience and culturally relevant solutions"
  },
  {
    name: "Photography",
    icon: "/assets/skills/photo.svg",
    desc: "Capturing life one snap at a time through a barrel of overpriced glass"
  },
  {
    name: "Video",
    icon: "/assets/skills/video.svg",
    desc: "Production, conceptualizing and imaginative storytelling in just the right lighting"
  },
  {
    name: "Sense of Humor",
    icon: "/assets/skills/humor.svg",
    desc: "A tendency of particular cognitive experiences to provoke laughter and provide amusement"
  }
];

export default function Skills() {
  return (
    <section id="skills" className="skills-simple-section container card">
      <h2 className="skills-simple-title">MY SKILLS</h2>
      <p className="skills-simple-subtitle">I do all kinds of neat stuff</p>
      <div className="skills-simple-grid">
        {SKILLS.map(skill => (
          <div className="skills-simple-item" key={skill.name}>
            <div className="skills-simple-icon">
              <img src={skill.icon} alt={skill.name} loading="lazy" />
            </div>
            <h3 className="skills-simple-name">{skill.name}</h3>
            <p className="skills-simple-desc">{skill.desc}</p>
          </div>
        ))}
      </div>
    </section>
  );
}
