import React, { useEffect } from 'react';
import Header from './components/Header';
import Projects from './components/Projects';
import './styles/main.css';

function App() {
  useEffect(() => {
    const sections = Array.from(document.querySelectorAll('section'));
    const observer = new IntersectionObserver(
      (entries, obs) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            const el = entry.target;
            el.classList.add('in-view');
            obs.unobserve(el);
          }
        });
      },
      { threshold: 0.15 }
    );

    sections.forEach((sec, idx) => {
      sec.classList.add('reveal');
      sec.style.transitionDelay = `${idx * 120}ms`;
      observer.observe(sec);

      const rect = sec.getBoundingClientRect();
      if (rect.top <= window.innerHeight * 0.85) {
        sec.classList.add('in-view');
        observer.unobserve(sec);
      }
    });

    return () => observer.disconnect();
  }, []);

  return (
    <div className="App">
      {/* Navigation bar */}
      <div className="navbar">
        <div className="navbar-inner">
          <div className="navbar-logo">PORTFOLIO</div>
          <ul className="navbar-links">
            <li><a href="#home" className="navbar-link">Home</a></li>
            <li><a href="#about" className="navbar-link">About</a></li>
            <li><a href="#skills" className="navbar-link">Skills</a></li>
            <li><a href="#projects" className="navbar-link active">Projects</a></li>
            <li><a href="#contact" className="navbar-link">Contact</a></li>
          </ul>
        </div>
      </div>

      <Header />
      <main>
        <section id="projects"><Projects /></section>
        <section id="contact"></section>
      </main>
      <footer className="footer">
        <div className="container">© {new Date().getFullYear()} Priyadharshini — Full‑Stack MERN & UX/UI Designer</div>
      </footer>
    </div>
  );
}

export default App;
