import React from 'react';

const SKILLS = [
  {
    name: "Photoshop",
    icon: "/assets/skills/photoshop.svg",
    level: 5,
    desc: "5+ years experience, used weekly"
  },
  {
    name: "Illustrator",
    icon: "/assets/skills/illustrator.svg",
    level: 4,
    desc: "Design and graphics, used for logo design"
  },
  {
    name: "Flash & Actionscript",
    icon: "/assets/skills/flash.svg",
    level: 3,
    desc: "10+ websites, used for games & banners"
  },
  {
    name: "HTML & CSS",
    icon: "/assets/skills/htmlcss.svg",
    level: 5,
    desc: "5+ years experience, used daily"
  },
  {
    name: "Javascript & jQuery",
    icon: "/assets/skills/js.svg",
    level: 4,
    desc: "3+ years experience, built interactive sites"
  },
  {
    name: "Classic ASP",
    icon: "/assets/skills/asp.svg",
    level: 3,
    desc: "3+ years experience"
  },
  {
    name: "SEO",
    icon: "/assets/skills/seo.svg",
    level: 4,
    desc: "3+ years experience, holding sites optimized"
  },
  {
    name: "HTML 5",
    icon: "/assets/skills/html5.svg",
    level: 5,
    desc: "Used for modern web apps"
  },
  {
    name: "ASP.net MVC (C#)",
    icon: "/assets/skills/aspnet.svg",
    level: 3,
    desc: "Used for enterprise solutions"
  }
];

export default function Skills() {
  return (
    <section id="skills" className="skills-bar-section container card">
      <h2 className="skills-bar-title">Skills & Competencies</h2>
      <div className="skills-bar-grid">
        {SKILLS.map(skill => (
          <div className="skills-bar-item" key={skill.name}>
            <div className="skills-bar-header">
              <div className="skills-bar-icon">
                <img src={skill.icon} alt={skill.name} loading="lazy" />
              </div>
              <div>
                <span className="skills-bar-name">{skill.name}</span>
                <span className="skills-bar-level">{skill.level}</span>
              </div>
            </div>
            <div className="skills-bar-desc">{skill.desc}</div>
            <div className="skills-bar-progress">
              <div
                className="skills-bar-fill"
                style={{ width: `${skill.level * 20}%` }}
              ></div>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}
