import React from 'react';

const SKILL_CATEGORIES = [
  {
    title: 'Frontend',
    items: [
      { name: 'React.js', level: 92 },
      { name: 'React Native', level: 82 },
      { name: 'HTML5', level: 95 },
      { name: 'CSS3', level: 88 },
      { name: 'JavaScript', level: 90 },
      { name: 'Tailwind CSS', level: 80 },
      { name: 'Bootstrap', level: 76 },
    ],
  },
  {
    title: 'Backend & APIs',
    items: [
      { name: 'Node.js', level: 84 },
      { name: 'Express.js', level: 82 },
      { name: 'REST APIs', level: 86 },
    ],
  },
  {
    title: 'Databases',
    items: [
      { name: 'MongoDB', level: 78 },
      { name: 'Mongoose', level: 75 },
    ],
  },
  {
    title: 'Design & UX',
    items: [
      { name: 'Figma', level: 88 },
      { name: 'Wireframing', level: 84 },
      { name: 'Prototyping', level: 82 },
      { name: 'UI/UX Principles', level: 86 },
    ],
  },
  {
    title: 'Tools & Practices',
    items: [
      { name: 'Git / GitHub', level: 88 },
      { name: 'Postman', level: 78 },
      { name: 'VS Code', level: 90 },
    ],
  },
];

export default function Skills() {
  return (
    <div className="container skills-card">
      <h2>Skills</h2>
      <p className="muted">Practical proficiency across Fullâ€‘Stack MERN, mobile (React Native) and UX/UI design.</p>

      <div className="skills-categories">
        {SKILL_CATEGORIES.map((cat) => (
          <div key={cat.title} className="skills-category">
            <h3>{cat.title}</h3>
            <div className="skills-list" role="list">
              {cat.items.map((s) => (
                <div
                  key={s.name}
                  className="skill-row"
                  role="listitem"
                  style={{ ['--level']: `${s.level}%` }}
                  aria-label={`${s.name}: ${s.level}%`}
                >
                  <div className="skill-top">
                    <div className="skill-name">{s.name}</div>
                    <div className="skill-percent" aria-hidden="true">{s.level}%</div>
                  </div>

                  <div className="skill-bar" aria-hidden="true">
                    <div className="skill-fill" />
                  </div>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>

      <p className="muted small-note">Levels are indicative. Replace or fine-tune percentages to reflect your confidence.</p>
    </div>
  );
}
