import React, { useEffect } from 'react';
import Header from './components/Header';
import Hero from './components/Hero';
import About from './components/About';
import Skills from './components/Skills';
import Projects from './components/Projects';
import Experience from './components/Experience';
import Education from './components/Education';
import Contact from './components/Contact';
import './styles/main.css';

function App() {
  useEffect(() => {
    const sections = Array.from(document.querySelectorAll('section'));
    const observer = new IntersectionObserver(
      (entries, obs) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            const el = entry.target;
            el.classList.add('in-view');
            obs.unobserve(el);
          }
        });
      },
      { threshold: 0.15 }
    );

    sections.forEach((sec, idx) => {
      sec.classList.add('reveal');
      sec.style.transitionDelay = `${idx * 120}ms`;
      observer.observe(sec);

      // IMMEDIATE CHECK: if section is already partly visible, show it
      const rect = sec.getBoundingClientRect();
      if (rect.top <= window.innerHeight * 0.85) {
        sec.classList.add('in-view');
        observer.unobserve(sec);
      }
    });

    return () => observer.disconnect();
  }, []);

  return (
    <div className="App">
      {/* animated background (pure CSS) */}
      <div className="bg-anim" aria-hidden="true">
        <div className="bg-gradient" />
        <div className="blob blob-1" />
        <div className="blob blob-2" />
        <div className="blob blob-3" />
        <div className="bg-dots" />
      </div>

      <Header />
      <main>
        <section id="home"><Hero /></section>
        <section id="about"><About /></section>
        <section id="skills"><Skills /></section>
        <section id="projects"><Projects /></section>
        <section id="experience"><Experience /></section>
        <section id="education"><Education /></section>
        <section id="contact"><Contact /></section>
      </main>
      <footer className="footer">
        <div className="container">© {new Date().getFullYear()} Priyadharshini — Full‑Stack MERN & UX/UI Designer</div>
      </footer>
    </div>
  );
}

export default App;
