import React from 'react';

/*
  Modern Skills Section:
  - Card layout with icons and subtle animation
  - Each category in its own card with a colored accent bar
  - Responsive grid, clean headings, and hover effects
*/

const SKILL_CATEGORIES = [
  {
    title: 'Frontend',
    color: '#2563eb',
    items: [
      { name: 'React', file: '/assets/skills/react.png' },
      { name: 'React Native', file: '/assets/skills/react-native.svg' },
      { name: 'HTML5', file: '/assets/skills/html5.png' },
      { name: 'CSS3', file: '/assets/skills/css3.svg' },
      { name: 'JavaScript', file: '/assets/skills/javascript.png' },
      { name: 'Tailwind', file: '/assets/skills/tailwind.svg' },
      { name: 'Bootstrap', file: '/assets/skills/bootstrap.svg' },
    ],
  },
  {
    title: 'Backend',
    color: '#059669',
    items: [
      { name: 'Node.js', file: '/assets/skills/nodejs.svg' },
      { name: 'Express', file: '/assets/skills/express.svg' },
      { name: 'MongoDB', file: '/assets/skills/mongodb.svg' },
      { name: 'Socket.io', file: '/assets/skills/socketio.svg' },
    ],
  },
  {
    title: 'Design',
    color: '#f59e42',
    items: [
      { name: 'Figma', file: '/assets/skills/figma.png' },
      { name: 'UI/UX', file: '/assets/skills/figma.png' },
    ],
  },
  {
    title: 'Tools',
    color: '#64748b',
    items: [
      { name: 'Git / GitHub', file: '/assets/skills/github.svg' },
      { name: 'Postman', file: '/assets/skills/postman.svg' },
    ],
  },
];

export default function Skills() {
  return (
    <section id="skills" className="skills-section container card skills-bg">
      <div className="skills-header">
        <h2>Skills</h2>
        <p className="muted">Technologies & Tools I Use</p>
      </div>
      <div className="skills-categories">
        {SKILL_CATEGORIES.map((cat) => (
          <div key={cat.title} className="skills-category" style={{ borderTop: `4px solid ${cat.color}` }}>
            <h3 className="skills-category-title" style={{ color: cat.color }}>{cat.title}</h3>
            <div className="skills-grid">
              {cat.items.map((s) => (
                <div
                  key={s.name}
                  className="skill-item"
                  tabIndex={0}
                  aria-label={s.name}
                  title={s.name}
                  style={{
                    boxShadow: `0 2px 12px ${cat.color}22`,
                    border: `1.5px solid ${cat.color}22`,
                    background: "rgba(255,255,255,0.95)",
                  }}
                >
                  <div className="skill-badge" style={{ border: `2px solid ${cat.color}`, background: "linear-gradient(135deg, #e0e7ef 0%, #fff 100%)" }}>
                    <img
                      src={s.file}
                      alt={`${s.name} logo`}
                      loading="lazy"
                      decoding="async"
                      style={{ filter: "drop-shadow(0 2px 8px #2563eb33)" }}
                      onError={(e) => (e.currentTarget.style.display = 'none')}
                    />
                    <div className="skill-fallback" aria-hidden="true">
                      {s.name.split(' ').map((p) => p[0]).slice(0, 2).join('')}
                    </div>
                  </div>
                  <div className="skill-name">{s.name}</div>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}
